package com.wanda.ccs.schedule.dao.impl;import org.springframework.stereotype.Repository;import com.wanda.ccs.model.TTaskOutRealTime;import com.wanda.ccs.schedule.dao.ITTaskOutRealTimeDao;import com.xcesys.extras.core.dao.impl.BaseDaoImpl;import com.xcesys.extras.core.util.DateUtil;@Repositorypublic class TTaskOutRealTimeDaoImpl extends BaseDaoImpl<TTaskOutRealTime>		implements ITTaskOutRealTimeDao {		public void create(String cinemaCode){		TTaskOutRealTime trt = new TTaskOutRealTime();		trt.setCode(cinemaCode);		trt.setTaskStatus("0");		trt.setCreateDate(DateUtil.getCurrentDate());		super.create(trt);	}}